mapper.update:

update replyboard
set order_no = order_no + 1
where group_no = #{groupNo} 
	  and order_no > #{orderNo} 
	  and depth <= #{depth}

mapper.select:

select max(order_no) orderNo
from replyboard
where depth = #{depth}

mapper.update:

update replyboard
set title = #{title},
	content = #{content}
where no = #{no}


update replyboard
set hit = hit + 1
where no = #{no}
